import React from "react";

class Categories extends React.Component {
    state = {
        activeItem: 2,
    }

    selectItemActive = (index) => {
        this.setState({
            activeItem: index
        })
    }

    render() {
        const {items, clickItem} = this.props
        return (
            <div>
                <div className="categories">
                    <ul>
                        <li>Все</li>

                        {items.map((name, i) => <li
                            className={this.state.activeItem === i ? 'active' : ''}
                            onClick={this.selectItemActive(i)}
                            key={`${name}_${i}`}
                        >
                            {name}</li>)}
                    </ul>
                </div>
            </div>
        )
    }
}

