import React from "react";

const Categories = ({items,item}) => {
const add = (name)=>{
    item(item)
    }
    return(
        <div>
            <div className="categories">
                <ul>
                    <li className="active">Все</li>

                    {items.map((name,i) => <li onClick={add} key={`${name}_${i}`}>{name}</li>)}
                </ul>
            </div>
        </div>
    )
}

export default Categories;
