import React, {useState} from "react";

const Categories = ({items,item}) => {

  // const [state,setState] = useState(initialState)
    const state = React.useState(null)

    const activeItem = state[0]
    const selectItemActive = state [1]

    console.log(activeItem,selectItemActive)

    return(
        <div>
            <div className="categories">
                <ul>
                    <li className="active">Все</li>

                    {items.map((name,i) => <li onClick={()=>selectItemActive(i)} key={`${name}_${i}`}>{name}</li>)}
                </ul>
            </div>
        </div>
    )
}

