import React from "react";

class Categories extends React.Component{
    state = {
        activeItem: 2,
    }
    render() {
        const {items,clickItem} = this.props
        return(
            <div>
                <div className={this.state.activeItem}>
                    <ul>
                        <li>Все</li>

                        {items.map((name,i) => <li
                            className="active"
                            onClick={()=>clickItem(name)}
                            key={`${name}_${i}`}
                        >
                            {name}</li>)}
                    </ul>
                </div>
            </div>
        )
    }
}

